import { useState } from 'react';

export default function BadStateCounter(){
    const [count, setCount] = useState(0);

    return(
        <div className="container">
            <h1 className="font-bold text-lg p-3">Count: {count}</h1>
            <button className="bg-blue-100 m-1 rounded px-1" 
                    onClick={() => {
                        setCount(count + 1);
                        setCount(count + 1);
                        setCount(count + 1);
                        }}>
                        +3
            </button>
        </div>
    );

}
