import { useState } from 'react';

let todoIndex = 0;
export default function SpreadList(){
    const [todo, setTodo] = useState('')
    const [todoList, setTodoList] = useState([])
    return(
        <div className='border rounded p-2'>
            <h1 className='font-bold text-lg'>Rendering lists with Spread</h1>
            <input 
                className='border rounded-md p-2 m-1'
                placeholder='todo list item'
                value={todo}
                onChange={(e) =>{
                    setTodo(e.target.value);
                }}
            />
            <button className='bg-cyan-200 rounded px-3 m-1'>Add</button>
        </div>
    );
}
