import { useState } from 'react';

const initialTabs = [
    {
        id: 0,
        name:"Featured",
        link:"link",
        isSelected: true,
    },
    {
        id: 1,
        name:"Recent",
        link:"link",
        isSelected: false,
    },
    {
        id: 2,
        name:"Trending",
        link:"link",
        isSelected: false,
    },
    {
        id: 3,
        name:"Your Work",
        link:"link",
        isSelected: false,
    }
];
export default function Tabs(){
    const [tabs, setTabs] = useState(initialTabs);
    const [selectedTab, setSelectedTab] = useState(tabs[0].id);

    function handleClick(e){
        if(e.target.isSelected === true){
            return;
        }
        else if(e.target.isSelected === false){
            let newTabs = tabs.map(tab => {
                if(tab.id === selectedTab){
                    setSelectedTab(tab.id);
                    return{
                        ...tab,
                        isSelected: true,
                    };
                }}
            );
            setTabs(newTabs);
        }

    }
    return(
        <div className='sliderContainer'>
            <div className='tabSlider'>
                {tabs.map(tab => {
                    <div key={tab.id} className={tab.isSelected ? 'tabLinkSelected' : 'tabLinkUnselected'}>
                        <button onClick={handleClick}>{tab.name}</button>
                    </div>

                })}
            </div>
        </div>
    );

}
